   361	        // Check authentication status
   362	        async function checkAuthStatus() {
   363	            const token = localStorage.getItem('authToken');
   364	            const userInfo = localStorage.getItem('userInfo');
   365	
   366	            if (!token || !userInfo) {
   367	                window.location.href = '../login.html';
   368	                return false;
   369	            }
   370	
   371	            const user = JSON.parse(userInfo);
   372	            if (!user.is_admin) {
   373	                localStorage.removeItem('authToken');
   374	                localStorage.removeItem('userInfo');
   375	                window.location.href = '../login.html';
   376	                return false;
   377	            }
   378	
   379	            try {
   380	                // Verify token with backend
   381	                const response = await fetch(`${API_BASE_URL}/auth/me`, {
   382	                    headers: {
   383	                        'Authorization': `Bearer ${token}`
   384	                    }
   385	                });
   386	
   387	                if (!response.ok) {
   388	                    localStorage.removeItem('authToken');
   389	                    localStorage.removeItem('userInfo');
   390	                    window.location.href = '../login.html';
   391	                    return false;
   392	                }
   393	
   394	                const data = await response.json();
   395	                if (!data.user.is_admin) {
   396	                    localStorage.removeItem('authToken');
   397	                    localStorage.removeItem('userInfo');
   398	                    window.location.href = '../login.html';
   399	                    return false;
   400	                }
   401	
   402	                return true;
   403	            } catch (error) {
   404	                console.error('Auth check failed:', error);
   405	                localStorage.removeItem('authToken');
   406	                localStorage.removeItem('userInfo');
   407	                window.location.href = '../login.html';
   408	                return false;
   409	            }
   410	        }
