  1324	    function addSelectedProduct() {
  1325	      const productName = DOM.productSearchInput.value.trim();
  1326	      const quantity = parseInt(DOM.quantityInput.value);
  1327	      // Get the price from the input field - this may be the original retail price
  1328	      // or a negotiated/discounted price entered by the user
  1329	      const price = Math.round(parseFloat(DOM.priceInput.value));
  1330	
  1331	      // Get the item_code and room_id from the dataset if available
  1332	      const itemCode = DOM.productSearchInput.dataset.itemCode;
  1333	      const roomId = DOM.productSearchInput.dataset.roomId;
  1334	
  1335	      // Find the product by item_code if available, otherwise fall back to finding by name
  1336	      let product;
  1337	      if (itemCode) {
  1338	        // Find the exact product by item_code (which is unique)
  1339	        product = allProducts.find(p => p.item_code === itemCode);
  1340	      } else {
  1341	        // Fall back to finding by name (for backward compatibility or manually entered products)
  1342	        product = allProducts.find(p => p.item_name.toLowerCase() === productName.toLowerCase());
  1343	      }
  1344	
  1345	      if (productName === '' || isNaN(quantity) || isNaN(price)) {
  1346	        showToastMessage('Please enter valid product details.', 'error');
  1347	        return;
  1348	      }
  1349	      if (quantity <= 0) {
  1350	        showToastMessage('Quantity must be at least 1.', 'error');
  1351	        return;
  1352	      }
  1353	      if (price < 0) {
  1354	        showToastMessage('Price cannot be negative.', 'error');
  1355	        return;
  1356	      }
  1357	      if (product && quantity > product.available_stock) {
  1358	        showToastMessage(`Cannot add ${quantity} units. Only ${product.available_stock} available in stock.`, 'error');
  1359	        return;
