   853	    function debounce(func, wait) {
   854	      let timeout;
   855	      return function executedFunction(...args) {
   856	        const later = () => {
   857	          clearTimeout(timeout);
   858	          func(...args);
   859	        };
   860	        clearTimeout(timeout);
   861	        timeout = setTimeout(later, wait);
   862	      };
   863	    }
