  1208	    function setCookie(name, value, days) {
  1209	      try {
  1210	        const d = new Date();
  1211	        d.setTime(d.getTime() + (days*24*60*60*1000));
  1212	        const expires = "expires="+ d.toUTCString();
  1213	        document.cookie = name + "=" + encodeURIComponent(value) + ";" + expires + ";path=/";
  1214	      } catch (e) {
  1215	        console.warn('Cookie not available, using storage');
  1216	        storage.set(name, value);
  1217	      }
  1218	    }
  1219	
  1220	    function getCookie(name) {
  1221	      try {
  1222	        const cname = name + "=";
  1223	        const decodedCookie = decodeURIComponent(document.cookie);
  1224	        const ca = decodedCookie.split(';');
  1225	        for(let i = 0; i < ca.length; i++) {
  1226	          let c = ca[i].trim();
  1227	          if (c.indexOf(cname) === 0) {
  1228	            return c.substring(cname.length, c.length);
  1229	          }
  1230	        }
  1231	        return "";
  1232	      } catch (e) {
  1233	        console.warn('Cookie not available, using storage');
  1234	        return storage.get(name) || "";
  1235	      }
  1236	    }
