  1208	    function setCookie(name, value, days) {
  1209	      try {
  1210	        const d = new Date();
  1211	        d.setTime(d.getTime() + (days*24*60*60*1000));
  1212	        const expires = "expires="+ d.toUTCString();
  1213	        document.cookie = name + "=" + encodeURIComponent(value) + ";" + expires + ";path=/";
  1214	      } catch (e) {
  1215	        console.warn('Cookie not available, using storage');
  1216	        storage.set(name, value);
  1217	      }
  1218	    }
