     5	  // Create users table if it doesn't exist
     6	  static async initializeUsersTable() {
     7	    const client = await pool.connect();
     8	    try {
     9	      await client.query(`
    10	        CREATE TABLE IF NOT EXISTS users (
    11	          id SERIAL PRIMARY KEY,
    12	          email VARCHAR(255) UNIQUE NOT NULL,
    13	          password_hash VARCHAR(255) NOT NULL,
    14	          is_admin BOOLEAN DEFAULT FALSE,
    15	          created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    16	          updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
    17	        )
    18	      `);
    19	
    20	      // Create default admin user if no users exist
    21	      const userCount = await client.query('SELECT COUNT(*) as count FROM users');
    22	      if (parseInt(userCount.rows[0].count) === 0) {
    23	        const hashedPassword = await bcrypt.hash('admin123', 10);
    24	        await client.query(
    25	          'INSERT INTO users (email, password_hash, is_admin) VALUES ($1, $2, $3)',
    26	          ['atlegangp851@gmail.com', hashedPassword, true]
    27	        );
    28	        console.log('Default admin user created: atlegangp851@gmail.com');
    29	      }
    30	    } finally {
    31	      client.release();
    32	    }
    33	  }
