  1337	      if (itemCode) {
  1338	        // Find the exact product by item_code (which is unique)
  1339	        product = allProducts.find(p => p.item_code === itemCode);
  1340	      } else {
  1341	        // Fall back to finding by name (for backward compatibility or manually entered products)
  1342	        product = allProducts.find(p => p.item_name.toLowerCase() === productName.toLowerCase());
  1343	      }
  1344	
  1345	      if (productName === '' || isNaN(quantity) || isNaN(price)) {
  1346	        showToastMessage('Please enter valid product details.', 'error');
  1347	        return;
  1348	      }
  1349	      if (quantity <= 0) {
  1350	        showToastMessage('Quantity must be at least 1.', 'error');
  1351	        return;
  1352	      }
  1353	      if (price < 0) {
  1354	        showToastMessage('Price cannot be negative.', 'error');
  1355	        return;
  1356	      }
  1357	      if (product && quantity > product.available_stock) {
  1358	        showToastMessage(`Cannot add ${quantity} units. Only ${product.available_stock} available in stock.`, 'error');
  1359	        return;
  1360	      }
  1361	
  1362	      // We'll store the typed-in price in product.price
  1363	      // Then let the table logic figure out net vs. tax if needed
  1364	      let taxPerProduct = 0;
  1365	      if (DOM.taxCheckbox.checked) {
  1366	        taxPerProduct = Math.round(price * TAX_RATE);
  1367	      }
  1368	
  1369	      if (product) {
  1370	        selectedProducts.push({
  1371	          item_code: product.item_code,
  1372	          name: productName,
  1373	          room_name: product.room_name, // Store room name for reference
  1374	          quantity: quantity,
  1375	          price: parseFloat(price),
  1376	          tax_per_product: taxPerProduct,
  1377	          is_new: false
  1378	        });
  1379	        product.available_stock -= quantity; // update stock
  1380	      } else {
  1381	        const item_code = `NEW-${newProductCounter++}`;
  1382	        selectedProducts.push({
  1383	          item_code: item_code,
  1384	          name: productName,
  1385	          room_name: 'N/A', // No room for new products
  1386	          quantity: quantity,
  1387	          price: parseFloat(price),
  1388	          tax_per_product: taxPerProduct,
  1389	          is_new: true
  1390	        });
  1391	      }
  1392	
  1393	      updateSelectedProductsTable();
  1394	      DOM.productSearchInput.value = '';
  1395	
  1396	      // Clear the stored item_code and room_id
  1397	      delete DOM.productSearchInput.dataset.itemCode;
  1398	      delete DOM.productSearchInput.dataset.roomId;
  1399	
  1400	      // Reset quantity input field
  1401	      const quantityInput = document.getElementById('quantity');
  1402	      quantityInput.value = '1';
  1403	      quantityInput.setAttribute('min', '1');
  1404	
  1405	      // Reset price and room fields
  1406	      DOM.priceInput.value = '0';
  1407	      DOM.roomInput.value = '';
  1408	      DOM.roomInput.classList.add('hidden');
  1409	      DOM.roomLabel.classList.add('hidden');
  1410	    }
  1411	
  1412	    /**
  1413	     * If tax is checked:
  1414	     *   - "Unit Price (R)" = price * 0.85
  1415	     *   - "Tax per Unit (R)" = price * 0.15
  1416	     *   - "Total (R)" = price * quantity
  1417	     *
  1418	     * If tax is NOT checked:
  1419	     *   - "Unit Price (R)" = price (as typed)
  1420	     *   - No separate tax column
  1421	     *   - "Total (R)" = price * quantity
  1422	     */
  1423	    function calculateAndDisplayTotal() {
  1424	      let subtotal = 0;
  1425	      let tax = 0;
  1426	      let total = 0;
  1427	
  1428	      selectedProducts.forEach(product => {
  1429	        if (DOM.taxCheckbox.checked) {
  1430	          subtotal += (product.price * (1 - TAX_RATE)) * product.quantity;
  1431	          tax += (product.price * TAX_RATE) * product.quantity;
  1432	          total += (product.price * product.quantity);
  1433	        } else {
  1434	          subtotal += (product.price * product.quantity);
  1435	          total += (product.price * product.quantity);
  1436	        }
  1437	      });
  1438	
  1439	      if (!DOM.taxCheckbox.checked) {
  1440	        tax = 0;
  1441	      }
  1442	
  1443	      DOM.subtotalAmountSpan.textContent = subtotal.toFixed(0);
  1444	      DOM.taxAmountSpan.textContent = tax.toFixed(0);
  1445	      DOM.totalAmountSpan.textContent = total.toFixed(0);
  1446	    }
