  1287	    function getCookie(name) {
  1288	      try {
  1289	        const cname = name + "=";
  1290	        const decodedCookie = decodeURIComponent(document.cookie);
  1291	        const ca = decodedCookie.split(';');
  1292	        for(let i = 0; i < ca.length; i++) {
  1293	          let c = ca[i].trim();
  1294	          if (c.indexOf(cname) === 0) {
