  1330	      const quantity = parseInt(DOM.quantityInput.value);
  1331	      // Get the price from the input field - this may be the original retail price
  1332	      // or a negotiated/discounted price entered by the user
  1333	      const price = Math.round(parseFloat(DOM.priceInput.value));
  1334	
  1335	      // Get the item_code and room_id from the dataset if available
  1336	      const itemCode = DOM.productSearchInput.dataset.itemCode;
  1337	      const roomId = DOM.productSearchInput.dataset.roomId;
  1338	
  1339	      // Find the product by item_code if available, otherwise fall back to finding by name
  1340	      let product;
  1341	      if (itemCode) {
  1342	        // Find the exact product by item_code (which is unique)
  1343	        product = allProducts.find(p => p.item_code === itemCode);
  1344	      } else {
  1345	        // Fall back to finding by name (for backward compatibility or manually entered products)
  1346	        product = allProducts.find(p => p.item_name.toLowerCase() === productName.toLowerCase());
  1347	      }
  1348	
  1349	      if (productName === '' || isNaN(quantity) || isNaN(price)) {
  1350	        showToastMessage('Please enter valid product details.', 'error');
  1351	        return;
  1352	      }
  1353	      if (quantity <= 0) {
  1354	        showToastMessage('Quantity must be at least 1.', 'error');
  1355	        return;
  1356	      }
  1357	      if (price < 0) {
  1358	        showToastMessage('Price cannot be negative.', 'error');
  1359	        return;
  1360	      }
  1361	      if (product && quantity > product.available_stock) {
  1362	        showToastMessage(`Cannot add ${quantity} units. Only ${product.available_stock} available in stock.`, 'error');
  1363	        return;
  1364	      }
  1365	
  1366	      // We'll store the typed-in price in product.price
  1367	      // Then let the table logic figure out net vs. tax if needed
  1368	      let taxPerProduct = 0;
  1369	      if (DOM.taxCheckbox.checked) {
  1370	        taxPerProduct = Math.round(price * TAX_RATE);
  1371	      }
  1372	
  1373	      if (product) {
  1374	        selectedProducts.push({
  1375	          item_code: product.item_code,
  1376	          name: productName,
  1377	          room_name: product.room_name, // Store room name for reference
  1378	          quantity: quantity,
  1379	          price: parseFloat(price),
  1380	          tax_per_product: taxPerProduct,
  1381	          is_new: false
  1382	        });
  1383	        product.available_stock -= quantity; // update stock
  1384	      } else {
  1385	        const item_code = `NEW-${newProductCounter++}`;
  1386	        selectedProducts.push({
  1387	          item_code: item_code,
  1388	          name: productName,
  1389	          room_name: 'N/A', // No room for new products
  1390	          quantity: quantity,
  1391	          price: parseFloat(price),
  1392	          tax_per_product: taxPerProduct,
  1393	          is_new: true
  1394	        });
  1395	      }
  1396	
  1397	      updateSelectedProductsTable();
  1398	      DOM.productSearchInput.value = '';
  1399	
  1400	      // Clear the stored item_code and room_id
  1401	      delete DOM.productSearchInput.dataset.itemCode;
  1402	      delete DOM.productSearchInput.dataset.roomId;
  1403	
  1404	      // Reset quantity input field
  1405	      const quantityInput = document.getElementById('quantity');
  1406	      quantityInput.value = '1';
  1407	      quantityInput.setAttribute('min', '1');
  1408	
  1409	      // Reset price and room fields
  1410	      DOM.priceInput.value = '0';
  1411	      DOM.roomInput.value = '';
  1412	      DOM.roomInput.classList.add('hidden');
  1413	      DOM.roomLabel.classList.add('hidden');
  1414	    }
