  1416	    /**
  1417	     * If tax is checked:
  1418	     *   - "Unit Price (R)" = price * 0.85
  1419	     *   - "Tax per Unit (R)" = price * 0.15
  1420	     *   - "Total (R)" = price * quantity
  1421	     *
  1422	     * If tax is NOT checked:
  1423	     *   - "Unit Price (R)" = price (as typed)
  1424	     *   - No separate tax column
  1425	     *   - "Total (R)" = price * quantity
  1426	     */
  1427	    function calculateAndDisplayTotal() {
  1428	      let subtotal = 0;
  1429	      let tax = 0;
  1430	      let total = 0;
  1431	
  1432	      selectedProducts.forEach(product => {
  1433	        if (DOM.taxCheckbox.checked) {
  1434	          subtotal += (product.price * (1 - TAX_RATE)) * product.quantity;
  1435	          tax += (product.price * TAX_RATE) * product.quantity;
  1436	          total += (product.price * product.quantity);
  1437	        } else {
  1438	          subtotal += (product.price * product.quantity);
  1439	          total += (product.price * product.quantity);
  1440	        }
  1441	      });
  1442	
  1443	      if (!DOM.taxCheckbox.checked) {
  1444	        tax = 0;
  1445	      }
  1446	
  1447	      DOM.subtotalAmountSpan.textContent = subtotal.toFixed(0);
  1448	      DOM.taxAmountSpan.textContent = tax.toFixed(0);
  1449	      DOM.totalAmountSpan.textContent = total.toFixed(0);
  1450	    }
