  1194	    function setCookie(name, value, days) {
  1195	      try {
  1196	        const d = new Date();
  1197	        d.setTime(d.getTime() + (days*24*60*60*1000));
  1198	        const expires = "expires="+ d.toUTCString();
  1199	        document.cookie = name + "=" + encodeURIComponent(value) + ";" + expires + ";path=/";
  1200	      } catch (e) {
  1201	        console.warn('Cookie not available, using storage');
  1202	        storage.set(name, value);
  1203	      }
  1204	    }
  1205	
  1206	    function getCookie(name) {
  1207	      try {
  1208	        const cname = name + "=";
  1209	        const decodedCookie = decodeURIComponent(document.cookie);
  1210	        const ca = decodedCookie.split(';');
  1211	        for(let i = 0; i < ca.length; i++) {
  1212	          let c = ca[i].trim();
  1213	          if (c.indexOf(cname) === 0) {
  1214	            return c.substring(cname.length, c.length);
  1215	          }
  1216	        }
  1217	        return "";
  1218	      } catch (e) {
  1219	        console.warn('Cookie not available, using storage');
  1220	        return storage.get(name) || "";
  1221	      }
  1222	    }
