  1220	    function getCookie(name) {
  1221	      try {
  1222	        const cname = name + "=";
  1223	        const decodedCookie = decodeURIComponent(document.cookie);
  1224	        const ca = decodedCookie.split(';');
  1225	        for(let i = 0; i < ca.length; i++) {
  1226	          let c = ca[i].trim();
  1227	          if (c.indexOf(cname) === 0) {
  1228	            return c.substring(cname.length, c.length);
  1229	          }
  1230	        }
  1231	        return "";
  1232	      } catch (e) {
  1233	        console.warn('Cookie not available, using storage');
  1234	        return storage.get(name) || "";
  1235	      }
  1236	    }
