  1349	    /**
  1350	     * If tax is checked:
  1351	     *   - "Unit Price (R)" = price * 0.85
  1352	     *   - "Tax per Unit (R)" = price * 0.15
  1353	     *   - "Total (R)" = price * quantity
  1354	     *
  1355	     * If tax is NOT checked:
  1356	     *   - "Unit Price (R)" = price (as typed)
  1357	     *   - No separate tax column
  1358	     *   - "Total (R)" = price * quantity
  1359	     */
  1360	    function calculateAndDisplayTotal() {
  1361	      let subtotal = 0;
  1362	      let tax = 0;
  1363	      let total = 0;
  1364	
  1365	      selectedProducts.forEach(product => {
  1366	        if (DOM.taxCheckbox.checked) {
  1367	          subtotal += (product.price * (1 - TAX_RATE)) * product.quantity;
  1368	          tax += (product.price * TAX_RATE) * product.quantity;
  1369	          total += (product.price * product.quantity);
  1370	        } else {
  1371	          subtotal += (product.price * product.quantity);
  1372	          total += (product.price * product.quantity);
  1373	        }
  1374	      });
  1375	
  1376	      if (!DOM.taxCheckbox.checked) {
  1377	        tax = 0;
  1378	      }
  1379	
  1380	      DOM.subtotalAmountSpan.textContent = subtotal.toFixed(0);
  1381	      DOM.taxAmountSpan.textContent = tax.toFixed(0);
  1382	      DOM.totalAmountSpan.textContent = total.toFixed(0);
  1383	    }
