     1	const jwt = require('jsonwebtoken');
     2	const UserModel = require('../models/userModel');
     3	
     4	const JWT_SECRET = process.env.JWT_SECRET || 'your-secret-key-change-in-production';
     5	
     6	// Middleware to verify JWT token
     7	const authenticateToken = async (req, res, next) => {
     8	  const authHeader = req.headers['authorization'];
     9	  const token = authHeader && authHeader.split(' ')[1]; // Bearer TOKEN
    10	
    11	  if (!token) {
    12	    return res.status(401).json({ message: 'Access token required' });
    13	  }
    14	
    15	  try {
    16	    const decoded = jwt.verify(token, JWT_SECRET);
    17	    const user = await UserModel.getUserById(decoded.userId);
    18	    
    19	    if (!user) {
    20	      return res.status(401).json({ message: 'Invalid token' });
    21	    }
    22	
    23	    req.user = user;
    24	    next();
    25	  } catch (error) {
    26	    return res.status(403).json({ message: 'Invalid or expired token' });
    27	  }
    28	};
    29	
    30	// Middleware to verify admin privileges
    31	const requireAdmin = async (req, res, next) => {
    32	  if (!req.user) {
    33	    return res.status(401).json({ message: 'Authentication required' });
    34	  }
    35	
    36	  if (!req.user.is_admin) {
    37	    return res.status(403).json({ message: 'Admin privileges required' });
    38	  }
    39	
    40	  next();
    41	};
    42	
    43	// Generate JWT token
    44	const generateToken = (userId) => {
    45	  return jwt.sign({ userId }, JWT_SECRET, { expiresIn: '24h' });
    46	};
    47	
