   299	    // Check authentication status
   300	    async function checkAuthStatus() {
   301	      const token = localStorage.getItem('authToken');
   302	      const userInfo = localStorage.getItem('userInfo');
   303	
   304	      if (!token || !userInfo) {
   305	        window.location.href = '../login.html';
   306	        return false;
   307	      }
   308	
   309	      const user = JSON.parse(userInfo);
   310	      if (!user.is_admin) {
   311	        localStorage.removeItem('authToken');
   312	        localStorage.removeItem('userInfo');
   313	        window.location.href = '../login.html';
   314	        return false;
   315	      }
   316	
   317	      try {
   318	        // Verify token with backend
   319	        const response = await fetch(`${API_BASE_URL}/auth/me`, {
   320	          headers: {
   321	            'Authorization': `Bearer ${token}`
   322	          }
   323	        });
   324	
   325	        if (!response.ok) {
   326	          localStorage.removeItem('authToken');
   327	          localStorage.removeItem('userInfo');
   328	          window.location.href = '../login.html';
   329	          return false;
   330	        }
   331	
   332	        const data = await response.json();
   333	        if (!data.user.is_admin) {
   334	          localStorage.removeItem('authToken');
   335	          localStorage.removeItem('userInfo');
   336	          window.location.href = '../login.html';
   337	          return false;
   338	        }
   339	
   340	        return true;
   341	      } catch (error) {
   342	        console.error('Auth check failed:', error);
   343	        localStorage.removeItem('authToken');
   344	        localStorage.removeItem('userInfo');
   345	        window.location.href = '../login.html';
   346	        return false;
   347	      }
   348	    }
