   421	    async function fetchRooms() {
   422	      try {
   423	        const response = await fetch(ROOMS_API_URL);
   424	        if (!response.ok) {
   425	          throw new Error(`Network response was not ok (${response.status})`);
   426	        }
   427	        const rooms = await response.json();
   428	
   429	        // Clear existing room blocks except the add-room block
   430	        roomGrid.querySelectorAll('.room-block:not(.add-room)').forEach(block => block.remove());
   431	
   432	        // Add each room to the grid
   433	        rooms.forEach(room => {
   434	          const roomElement = createRoomBlock(room);
   435	          roomGrid.insertBefore(roomElement, addRoomBlock);
   436	        });
   437	      } catch (error) {
   438	        console.error('Error fetching rooms:', error);
   439	        showToast('Failed to load rooms. Please try again later.', 'error');
   440	      }
   441	    }
