Path: Admin/auth.js
     1	// Authentication utility for admin pages
     2	const API_BASE_URL = 'https://shans-backend-1.onrender.com/api';
     3	
     4	// Check authentication status
     5	function checkAuthStatus() {
     6	  const token = localStorage.getItem('authToken');
     7	  const userInfo = localStorage.getItem('userInfo');
     8	  
     9	  if (!token || !userInfo) {
    10	    window.location.href = '../login.html';
    11	    return false;
    12	  }
    13	
    14	  const user = JSON.parse(userInfo);
    15	  if (!user.is_admin) {
    16	    localStorage.removeItem('authToken');
    17	    localStorage.removeItem('userInfo');
    18	    window.location.href = '../login.html';
    19	    return false;
    20	  }
    21	
    22	  // Verify token with backend
    23	  fetch(`${API_BASE_URL}/auth/me`, {
    24	    headers: {
    25	      'Authorization': `Bearer ${token}`
    26	    }
    27	  })
    28	  .then(response => {
    29	    if (!response.ok) {
    30	      // Token is invalid, redirect to login
    31	      localStorage.removeItem('authToken');
    32	      localStorage.removeItem('userInfo');
    33	      window.location.href = '../login.html';
    34	    } else {
    35	      return response.json();
    36	    }
    37	  })
    38	  .then(data => {
    39	    if (data && !data.user.is_admin) {
    40	      // User is not admin, redirect to login
    41	      localStorage.removeItem('authToken');
    42	      localStorage.removeItem('userInfo');
    43	      window.location.href = '../login.html';
    44	    }
    45	  })
    46	  .catch(error => {
    47	    console.error('Auth check failed:', error);
    48	    localStorage.removeItem('authToken');
    49	    localStorage.removeItem('userInfo');
    50	    window.location.href = '../login.html';
    51	  });
    52	
    53	  return true;
    54	}
    55	
    56	// Logout functionality
    57	function logout() {
    58	  fetch(`${API_BASE_URL}/auth/logout`, {
    59	    method: 'POST',
    60	    headers: {
    61	      'Authorization': `Bearer ${localStorage.getItem('authToken')}`
    62	    }
    63	  })
    64	  .then(() => {
    65	    localStorage.removeItem('authToken');
    66	    localStorage.removeItem('userInfo');
    67	    window.location.href = '../login.html';
    68	  })
    69	  .catch(error => {
    70	    console.error('Logout error:', error);
    71	    // Even if logout fails on server, clear local storage and redirect
    72	    localStorage.removeItem('authToken');
    73	    localStorage.removeItem('userInfo');
    74	    window.location.href = '../login.html';
    75	  });
    76	}
    77	
    78	// Initialize authentication on page load
    79	function initAuth() {
    80	  if (!checkAuthStatus()) {
    81	    return false;
    82	  }
    83	  return true;
    84	}
